using System;
using System.Collections.Generic;
using System.IO;
using System.Numerics;

/*
You are given two numbers, n and k. You have to calculate the super digit of n when the concatenated k times. 
That is, if n = 148 and k = 3 => 1+4+8+1+4+8+1+4+8->39->12->3.
Consider this test case:  100000=k -> 3
*/

class Solution {
    
    static string SuperDigit(string str) {
        //base case:
        if(str.Length < 2)
            return str;
        //recursive case:
        return SuperDigit(DigitSum(str).ToString());
    }
    static BigInteger DigitSum(string str) {
        BigInteger sum = 0;
        for(int i = 0; i < str.Length; i++) {
            sum += (int)Char.GetNumericValue(str[i]);
        }
        return sum;
    }
    
    static void Main(String[] args) {
        string[] input = Console.ReadLine().Split(' ');
        int k = Convert.ToInt32(input[1]);
        Console.WriteLine(SuperDigit((DigitSum(input[0])*k).ToString()));
        
        /* There is also a pure math solution: http://applet-magic.com/digitsummod9.htm
        var x = (DigitSum(input[0])*k) % 9;
        Console.WriteLine("{0}", x!=0 ? x : 9);*/
        
    }
}